const mutations = {
  SET_TOKEN_ROLE(state, payload) {
    localStorage.setItem("token", payload.token);
    localStorage.setItem("role_id", role);
    state.token = payload.token;
    state.role = payload.role;
    state.isLoggedIn = true;
  },
};
export default mutations;
